class PhotosController < ApplicationController
  include PhotosHelper

  before_action :set_user, :authenticate
  before_action :set_photo, only: [:edit, :update, :destroy, :show]

  respond_to :json

  def_param_group :photos_param do
    param :photo, Hash do
      param :name, String, required: false
      param :image, String, required: true
    end
  end

  api :GET, 'users/:user_id/photos', 'Retrieve all photos from a user'
  description "
  <b>Headers</b>

  Content-type: application/json

  Authorization: Token token=<remember_token>"
  example "
  Response:
  {
    'user_id': 1,
    'photos': [
        {
            'photo_id': 1,
            'name': 'test1.jpeg'
        },
        {
            'photo_id': 2,
            'name': 'test2.jpeg'
        },
        {
            'photo_id': 3,
            'name': 'test3.jpeg'
        },
        {
            'photo_id': 4,
            'name': 'test4.jpeg'
        },
        {
            'photo_id': 5,
            'name': 'test5.jpeg'
        }
    ]
  }"
  def index
    @photos = @user.photos
  end

  api :GET, '/users/:user_id/photos/:photo_id', 'Get info of a photo'
  description "
  <b>Headers</b>

  Content-type: application/json

  Authorization: Token token=<remember_token>"
  example "
  Response:
  {
    'photo_id': 1,
    'name': test1.jpeg',
    'image':''',
    'date': '2013-07-24T09:12:13.273Z'
  }"
  def show
    retrievePictureFromFTP @user.user_id, @photo.name
    @photo = @user.photos.find_by_photo_id(params[:photo_id])
    return render action: 'show'
  end


  # GET /photos/1/edit
  def edit
  end


  api :POST, '/users/:user_id/photos', 'Add a photo. *Base64 picture string must be in one line string.'
  formats ['json']
  description "
  <b>Headers</b>

  Content-type: application/json

  Authorization: Token token=<remember_token>"
  param_group :photos_param
  example "
  Request body:
  {
    'photo':{
      'name':myphoto.jpeg',
      'image':'/9j/4AAQSkZJRgABAgAAZABkAAD/7AARRHVja3kAAQAEAAAASAAA'
    }
  }

  Response:
  {
    'photo_id': 6,
    'name': 'photourl.jpg',
    'image':''',
    'date': '2013-07-24T09:12:13.273Z'
  }"
  def create
    @picture = params[:photo][:image]
    uploaded = uploadPictureToFTP @user.user_id, params[:photo][:name],  params[:photo][:image], @user.photo_num
    if !uploaded.has_key?(:error)
      @photo = @user.photos.create(name: params[:photo][:name])
      if @photo.save
        return render action: 'show', status: :created
      else
        return render json: {exception: 'PhotoException', message: @photo.errors.full_messages}, status: 400
      end
    else
      return render json: {exception: 'PhotosExceptionception', message: "#{uploaded[:error]}"}, status: 400
    end
  end

  # PATCH/PUT /photos/1
  # PATCH/PUT /photos/1.json
  # NOT USED!
  def update
    if @photo.update(photo_params)
      return render json: {info: 'Photo successfully updated'}
    else
      return render json: {exception: 'PhotoException', message: @photo.errors.full_messages}, status: 400
    end
  end

  api :DELETE, 'users/:user_id/photos/:photo_id', 'Remove a photo from user\'s album'
  description "
  <b>Headers</b>

  Content-type: application/json

  Authorization: Token token=<remember_token>"
  example "
  {
    'info': 'Photo removed successfully'
  }"
  def destroy
    removed = removePictureFromFTP @user.user_id, @photo.name
    if !removed.has_key?(:error)
      if @photo.destroy
        return render json: {info:"Photo removed successfully"}
      else
        return render json: {exception: 'PhotoException', message: @photo.errors.full_messages}, status: 400
      end
    else
      return render json: {exception: 'PhotoException', message: "#{removed[:error]}"}, status: 400
    end
  end

  private

    # Defines the user that correspondes to the given urser_id
    def set_user
     begin
        @user = User.find(params[:user_id])
      rescue
        return render json: {exception: "PhotosException", message: "This user doesn't exist"}, status: 400
      end
    end

    # Use callbacks to share common setup or constraints between actions.
    def set_photo
      begin
        @photo = Photo.find(params[:photo_id])
      rescue
        return render json: {exception: "PhotosException", message: "This photo doesn't exist"}, status: 400
      end
    end

    # Never trust parameters from the scary internet, only allow the white list through.
    def photo_params
      params.require(:photo).permit(:name, :image)
    end
end
